import React, { Component } from 'react';
import axios from 'axios';
class Market extends Component {
  constructor() {
    super();
    this.state = {
      ticker: '',
    };
  } componentDidMount(){
  axios.get('https://indodax.com/api/ticker/btcidr')
  .then((ambilData) => {
    console.log(ambilData);
    this.setState({
      data1: ambilData.data.ticker.last,
      data2: ambilData.data.ticker.low,
      data3: ambilData.data.ticker.high,
      data4: ambilData.data.ticker.vol_btc
    }) 
  })
};
render() {
  var css = {border:'2px solid black', padding:'12px'}
  
  return (
    <div>
      <center>
        <h1>Tabel Bitcoin Market</h1>
        <table style={css}>
          <tr>
    <th> Nama </th>
    <th> TTL </th> 
    <th> Alamat </th>
  </tr>
  <tr>
    <td> Galih </td>
    <td> Jepara, 25 Jan 92 </td> 
    <td> Soditan 02/VI Kramat Jati </td>
  </tr>
  <tr>
    <td> Ratna </td>
    <td> Bandung, 4 Apr 1994 </td> 
    <td> Geger Kalong No 45 </td>
  </tr>

        </table>
      </center>
    </div>
  );
}
}
export default Market;
