import React, { Component } from "react";
import axios from "axios";
class App extends Component {
  constructor() {
    super();
    this.state = {
      dataKu: [],
    };
  }
  componentDidMount() {
    axios
      .get("https://indodax.com/api/ticker/btcidr")
      .then((ambilData) => {
        console.log(ambilData);
        this.setState({
          dataKu: ambilData.data,
        });
      });
  }
  render() {
    var css = { border: "1px solid black", padding: "12px" };
    const data = this.state.dataKu.map((item, index) => {
      var ticker = [
        item.ticker.high,
        item.ticker.low,
        item.ticker.vol_btc,
        item.ticker.vol_idr,
        item.ticker.last,
        item.ticker.buy,
        item.ticker.sell,
        item.ticker.server_time,
      ].join(", ");
      return (
        <tr style={css} key={index}>
          <td style={css}>{ticker}</td>
        </tr>
      );
    });
    return (
      <div>
        <center>
          <h1>Coba Get Data</h1>
          <table style={css}>
            <tbody>
              <tr>
                <th>Ticker</th>
              </tr>
              {data}
            </tbody>
          </table>
        </center>
      </div>
    );
  }
}
export default App;
