import React, { Component } from 'react';
import axios from 'axios';
class Market extends Component {
  constructor() {
    super();
    this.state = {
      ticker: '',
    };
  } componentDidMount(){
  axios.get('https://indodax.com/api/ticker/btcidr')
  .then((ambilData) => {
    console.log(ambilData);
    this.setState({
      data1: ambilData.data.ticker.last,
      data2: ambilData.data.ticker.low,
      data3: ambilData.data.ticker.high,
      data4: ambilData.data.ticker.vol_btc
    }) 
  })
};
render() {
  var css = { border: "1px solid black", padding: "12px" };
  const data = this.state.dataKu.map((item, index) => {
    var id = item.id;
    var name = item.name;
    var mail = item.email;
    var alamat = [
      item.address.suite,
      item.address.street,
      item.address.city,
    ].join(", ");
    return (
      <tr style={css} key={index}>
        <td style={css}>{id}</td>
        <td style={css}>{name}</td>
        <td style={css}>{mail}</td>
        <td style={css}>{alamat}</td>
      </tr>
    );
  });

  return (
    <div>
      <center>
        <h1>Tabel Bitcoin Market</h1>
        <table style={css}>
          <tbody>
            <tr>
            <th>Harga Terakhir</th>
            <th>Low</th>
            <th>High</th>
            <th>Vol BTC</th>
            </tr>
        {this.state.data1}
        {this.state.data2}
        {this.state.data3}
        {this.state.data4}
        </tbody>
        </table>
      </center>
    </div>
  );
}
}
export default Market;
