import React, { Component } from "react";
import axios from "axios";
class App extends Component {
  constructor() {
    super();
    this.state = {
      dataKu: [],
    };
  }
  componentDidMount() {
    axios
      .get("https://indodax.com/api/depth/btcidr")
      .then((ambilData) => {
        console.log(ambilData);
        this.setState({
          dataKu: ambilData.data,
        });
      });
  }
  render() {
    var css = { border: "1px solid black", padding: "12px" };
    const data = this.state.dataKu.map((item, index) => {
      var buy = item.buy;
      // var name = item.name;
      // var mail = item.email;
      // var alamat = [
      //   item.address.suite,
      //   item.address.street,
      //   item.address.city,
      // ].join(", ");
      return (
        <tr style={css} key={index}>
          {/* <td style={css}>{buy}</td>
          <td style={css}>{name}</td>
          <td style={css}>{mail}</td>
          <td style={css}>{alamat}</td> */}
        </tr>
      );
    });
    return (
      <div>
        <center>
          <h1>Market Jual</h1>
          <table style={css}>
            <tbody>
              <tr>
                <th>No</th>
                <th>Harga</th>
                <th>BTC</th>
                <th>IDR</th>
              </tr>
              {data}
            </tbody>
          </table>
        </center>
      </div>
    );
  }
}
export default App;
