import React, { Component } from "react";
import axios from "axios";
import "./../App.css";

class Market extends Component {
  constructor() {
    super();
    this.state = {
      ticker: "",
    };
  }
  componentDidMount() {
    axios.get("https://indodax.com/api/ticker/btcidr").then((ambilData) => {
      console.log(ambilData);
      this.setState({
        data1: ambilData.data.ticker.last,
        data2: ambilData.data.ticker.low,
        data3: ambilData.data.ticker.high,
        data4: ambilData.data.ticker.vol_btc,
      });
    });
  }
  render() {
    var css = { border: "1px solid black", padding: "10px" };
    return (
      <div>
        <center>
          <h1>Bitcoin Market</h1>
          <table style={css}>
            <tbody>
              <tr>
                <td>{this.state.data1} <br/> Harga Terakhir</td>
                <td>{this.state.data2} <br/> Low </td>
                <td>{this.state.data3} <br/> High </td>
              </tr>
              <td colSpan="3">
                Vol 24h : <b>{this.state.data4}</b> BTC
              </td>
            </tbody>
          </table>
        </center>
      </div>
    );
  }
}
export default Market;
