import React, { Component } from "react";
import axios from "axios";

class Jual extends Component {
  constructor() {
    super();
    this.state = {
      sell: [],
    };
  }

  componentDidMount() {
    axios.get("https://indodax.com/api/ticker/btcidr").then((ambilData) => {
      console.log(ambilData);
      this.setState({ Sell: ambilData.data.sell });
    });
  }
  render() {
    const data = this.state.sell.map((sell) => {
      return (
        <tr>
          <td>{sell[0].toLocaleString("Id-ID")}</td>
          <td>{parseFloat(sell[1]).replace(".", ",")}</td>
          <td>
            {Math.floor(sell[0] * parseFloat(sell[1])).toLocaleString("id-ID")}
          </td>
        </tr>
      );
    });
    return (
      <div className="Market-table align-middle">
        <p>Market Jual</p>
        <table className="table table-bordered">
          <thead data-bs-spy="scroll">
            <tr>
              <th>Harga</th>
              <th>BTC</th>
              <th>IDR</th>
            </tr>
            {data}
          </thead>
        </table>
      </div>
    );
  }
}
export default Jual;
