class App extends React.Component {
  state = {
    data: [1, 2, 3, 4, 5, 6],
  };
  renderKotak = () => {
    return this.state.data.map((val, index) => {
      return (
        <View
          style={{
            backgroundColor: index % 2 === 1 ? "teal" : "orange",
            height: 100,
            width: "40%",
            marginHorizontal: 5,
            marginVertical: 5,
            textAlign: "center",
            justifyContent: "center",
          }}
        ></View>
      );
    });
  };
  render() {
    return (
      <View style={styles.app}>
        <View style={styles.kotak}>{this.renderKotak()}</View>
      </View>
    );
  }
}

export default App;
