import React, { Component } from 'react';
import axios from 'axios';

class Sell extends Component {
  constructor() {
    super();
    this.state = {
      ticker: [],
    };
}componentDidMount(){
  axios.get('https://indodax.com/api/depth/btcidr')
  .then((ambilData) => {
    console.log(ambilData);
    this.setState({
      ticker: ambilData.data.sell,
      ticker: ambilData.data.buy,
    }) 
  })
};
render() {
    var css = {border:'1px solid black', padding:'12px'}
    const data = this.state.ticker.map((item, index)=>{
        var harga = item[0]
        var btc = item[1]
        var idr = item[0]*item[1]
     return <tr style={css} key={index}>
     <td style={css}>{harga}</td>
     <td style={css}>{btc}</td>
     <td style={css}>{idr}</td>
     </tr>;
     
    }) 
    return (
     <div>
       <center>
          <h1>Market Jual</h1>
          <table style={css}>
          <tbody>
              <tr>
          <th>Harga</th>
            <th>BTC</th>
            <th>IDR</th>
            </tr>
            { data }
            </tbody>
          </table>
       </center>
       <center>
          <h1>Market Beli</h1>
          <table style={css}>
          <tbody>
              <tr>
          <th>Harga</th>
            <th>BTC</th>
            <th>IDR</th>
            </tr>
            { data }
            </tbody>
          </table>
       </center>
     </div>
    );
   }
   }export default Sell;
