import React, { Component } from 'react';
import axios from 'axios';

class Sale extends Component {
  constructor() {
    super();
    this.state = {
      ticker: [],
    };
}componentDidMount(){
  axios.get('https://indodax.com/api/depth/btcidr')
  .then((ambilData) => {
    console.log(ambilData);
    this.setState({
      ticker: ambilData.data.buy,
    }) 
  })
};
render() {
    const data = this.state.ticker.map((item, index)=>{
     var fullfilm = [item[0], item[1]].join(" - ");
     return <p key={index}>{fullfilm}</p>;
    })
    return (
     <div>
       <center>
          <h1>Coba Get Data</h1>
         { data }
       </center>
     </div>
    );
   }
   }export default Sale;
