import React, { Component } from "react";
import axios from "axios";

class Buy extends Component {
  constructor() {
    super();
    this.state = {
      ticker: [],
    };
  }
  componentDidMount() {
    axios.get("https://indodax.com/api/depth/btcidr").then((ambilData) => {
      console.log(ambilData);
      this.setState({
        ticker: ambilData.data.buy,
      });
    });
  }
  render() {
    var css = { border: "1px solid black", padding: "12px" };
    const data = this.state.ticker.map((item, index) => {
      var harga = item[0].toLocaleString("id-ID");
      var btc = (parseFloat(item[1])).toString().replace('.', ',');
      var idr = (item[0] * (parseFloat(item[1]))).toLocaleString("id-ID");
      var num_array = idr.split(',');
      var num_string = num_array.join('');
      return (
        <tr style={css} key={index}>
          <td style={css}>{harga}</td>
          <td style={css}>{btc}</td>
          <td style={css}>{num_string}</td>
        </tr>
      );
    });

    return (
      <div>
        <center>
          <h1>Market Beli</h1>
          <table className="tabel-buy" style={css}>
            <tbody>
              <tr className="awal">
                <th>Harga</th>
                <th>BTC</th>
                <th>IDR</th>
              </tr>
              {data}
            </tbody>
          </table>
        </center>
      </div>
    );
  }
}
export default Buy;
