import React, { Component } from 'react';
import axios from 'axios';
import './../App.css';

class Market extends Component {
  constructor() {
    super();
    this.state = {
      ticker: '',
    };
  } componentDidMount(){
  axios.get('https://indodax.com/api/ticker/btcidr')
  .then((ambilData) => {
    console.log(ambilData);
    this.setState({
      data1: ambilData.data.ticker.last,
      data2: ambilData.data.ticker.low,
      data3: ambilData.data.ticker.high,
      data4: ambilData.data.ticker.vol_btc
    }) 
  })
};
render() {
  var css = {border:'1px solid black', padding:'10px'}
  return (
    <div>
      <center>
        <h1>Bitcoin Market</h1>
        <table style={css}>
          <tbody>
            <tr>
            <th>Harga Terakhir</th>
            <th>Low</th>
            <th>High</th>
            </tr>
            <tr>
              <td>{this.state.data1}<br/></td>
              <td>{this.state.data2}<br/></td>
              <td>{this.state.data3}<br/></td>
            </tr>
            <th>Vol BTC</th>
            <td colSpan="2"><br/>{this.state.data4}</td>
        </tbody>
        </table>
      </center>
    </div>
  );
}
}
export default Market;
