import React, { Component } from "react";
import axios from "axios";

class Coba extends Component {
  constructor() {
    super();
    this.state = {
      dataKu: [],
    };
  }

  componentDidMount() {
    axios.get("https://indodax.com/api/ticker/btcidr").then((ambilData) => {
      console.log(ambilData);
      this.setState({
        dataKu: ambilData.data,
      });
    });
  }
  render() {
    var css = { border: "1px solid black", padding: "12px" };
    const data = this.state.dataKu.map((item, index) => {
      return (
        <tr style={css} key={index}>
          <td style={css}>{item.buy}</td>
          <td style={css}>{item.vol_btc}</td>
          <td style={css}>{item.vol_idr}</td>
        </tr>
      );
    });
    return (
      <div>
        <center>
          <h1>Coba Get Data</h1>
          <table style={css}>
            <tbody>
              <tr>
                <th>Harga</th>
                <th>BTC</th>
                <th>IDR</th>
              </tr>
              {data}
            </tbody>
          </table>
        </center>
      </div>
    );
  }
}
export default Coba;
